<?phpsession_start();require_once "../util/Session.php";require_once "../util/ChromePhp.php";require_once "../modelo/TramiteModelo.php";$variable = $_REQUEST["variable"];switch ($variable) {    case 'loginUsuario':        try {            $user = trim($_POST['user']);            $pwd = trim($_POST['pwd']);            $modelo = new TramiteModelo();            $rec = $modelo->validarLoginUsuario($user, $pwd);            if ($rec != null) {                $rs['usu_cod'] = $rec->Fields('usu_cod');                $rs['usu_login'] = $rec->Fields('usu_login');                $rs['usu_pass'] = $rec->Fields('usu_pass');                $rs['fun_cod'] = $rec->Fields('fun_cod');                $rs['usu_nom'] = $rec->Fields('usu_nom');                $rs['loc_cod'] = $rec->Fields('loc_cod');                $rs['loc_des'] = $rec->Fields('loc_des');                Session::setAttribute("beanUsuario", $rs);                $rpt = 'OK';            }        } catch (Exception $e) {            $rpt = $e->getMessage();        }        echo $rpt;        break;    case 'listadoUbigeo':        try {            $id = $_REQUEST['id'];            $modelo = new TramiteModelo();            $rec = $modelo->listadoDistritoLima();            $opt = "";            while (!$rec->EOF) {                if ($rec->Fields('id') == $id) {                    $cmd = " selected";                } else {                    $cmd = "";                }                $opt .= "<option value=" . $rec->Fields('id') . $cmd . ">" . utf8_encode($rec->Fields('descripcion')) . "</option>";                $rec->MoveNext();            }            echo $opt;        } catch (Exception $e) {            $id = $e->getMessage();            echo $id;        }        break;    case 'listadoOperadorMovil':        try {            $modelo = new TramiteModelo();            $rec = $modelo->listadoOperadorMovil();            $opt = "<option value='0'>SELECCIONE</option>";            while (!$rec->EOF) {                $opt .= "<option value=" . $rec->Fields('ope_id') . ">" . utf8_encode($rec->Fields('ope_des')) . "</option>";                $rec->MoveNext();            }            echo $opt;        } catch (Exception $e) {            $id = $e->getMessage();            echo $id;        }        break;    case 'buscaAlumno':        try {            $nombre = $_REQUEST['term'];            $modelo = new TramiteModelo();            $rec = $modelo->buscarAlumno($nombre);            if ($rec->RecordCount() != 0) {                while (!$rec->EOF) {                    $datos[] = array(                        "value" => utf8_encode(stripslashes($rec->Fields('nombre'))),                        "alu_cod" => $rec->Fields('alu_cod'), // alu_cod del alumno                        "alu_cba" => $rec->Fields('alu_cba'), //                        "alu_pat" => utf8_encode(stripslashes($rec->Fields('alu_pat'))), // apellido paterno                        "alu_mat" => utf8_encode(stripslashes($rec->Fields('alu_mat'))), // apellido materno                        "alu_nom" => utf8_encode(stripslashes($rec->Fields('alu_nom'))), // nombre                        "alu_dni" => $rec->Fields('alu_dni'), // dni del alumno                        "alu_dir" => utf8_encode(stripslashes($rec->Fields('alu_dir'))), // direccion del alumno                        "dis_cod" => $rec->Fields('dis_cod'), // id del distrito                        "alu_cor" => utf8_encode(stripslashes($rec->Fields('alu_correo'))), // id correo electronico                        "alu_ubi" => $rec->Fields('alu_ubi') // id correo electronico                    );                    $rec->MoveNext();                }            }            echo json_encode($datos);        } catch (Exception $e) {            $rpt = $e->getMessage();            echo $rpt;        }        break;    case 'listadoTelefonoAlumno':        try {            $codigo = $_REQUEST['aluCod'];            $tipo = "A";            $modelo = new TramiteModelo();            $rec = $modelo->listadoTelefono($tipo, $codigo);            ChromePhp::log($rec);            $div = "<div class='col-sm-12'><</div>";            while (!$rec->EOF) {                $opt .= "<option value=" . $rec->Fields('ope_id') . ">" . utf8_encode($rec->Fields('ope_des')) . "</option>";                $rec->MoveNext();            }            echo $opt;        } catch (Exception $e) {            $rpt = $e->getMessage();            echo $rpt;        }        break;}