<?phprequire_once "../util/ChromePhp.php";require_once "../ds/AccesoBD.php";class TramiteDAO{    public function listadoDistritoLima()    {        try {            $sql = "SELECT * FROM tb_ubigeo WHERE SUBSTR(id,1,4) ='1401' AND LENGTH(id) = 6 ORDER BY descripcion ASC";            $cn = AccesoBD::getInstancia();            $lista = $cn->Execute($sql);            $rec = null;            if ($lista->RecordCount() != 0) {                $rec = $lista;            }            return $rec;        } catch (Exception $e) {            throw $e;        }    }    public function listadoOperadorMovil()    {        try {            $sql = "SELECT ope_id,ope_des FROM vw_ts_operador_movil";            $cn = AccesoBD::getInstancia();            $lista = $cn->Execute($sql);            $rec = null;            if ($lista->RecordCount() != 0) {                $rec = $lista;            }            return $rec;        } catch (Exception $e) {            throw $e;        }    }    public function validaLoginUsuario($user)    {        try {            $sql = "CALL sp_ts_busca_usuario_clave('" . $user . "')";            $cn = AccesoBD::getInstancia();            $lista = $cn->Execute($sql);            $rec = null;            if ($lista->RecordCount() == 1) {                $rec = $lista;            }            return $rec;        } catch (Exception $e) {            throw $e;        }    }    public function buscarAlumno($nombre)    {        try {            $sql = "CALL sp_ts_buscar_alumno('" . $nombre . "')";            $cn = AccesoBD::getInstancia();            $lista = $cn->Execute($sql);            $rec = null;            if ($lista->RecordCount() != 0) {                $rec = $lista;            }            return $rec;        } catch (Exception $e) {            throw $e;        }    }    public function listadoTelefono($tipo, $codigo)    {        try {            $sql = "CALL sp_ts_buscar_telefono(" . $tipo . "," . $codigo . ")";            $cn = AccesoBD::getInstancia();            $lista = $cn->Execute($sql);            $rec = null;            if ($lista->RecordCount() != 0) {                $rec = $lista;            }            return $rec;        } catch (Exception $e) {            throw $e;        }    }}