<?php// Esta clase permite gestionar las variables de sesionclass Session{// Verifica si existe una var de sesion    public static function existsAttribute($name)    {        $rpta = FALSE;        if (isset($_SESSION[$name])) {            $rpta = TRUE;        }        return $rpta;    }// Retorna el valor de una variable de sesion    public static function getAttribute($name)    {        $value = null;        if (self::existsAttribute($name)) {            $value = $_SESSION[$name];        }        return $value;    }// Guada un valor en una variable de sesion    public static function setAttribute($name, $value)    {        $_SESSION[$name] = $value;    }// Eliminar una var de sesion    public static function removeAttribute($name)    {        unset($_SESSION[$name]);    }// Retorna el valor de una variable de sesion    public static function getAttributeRemove($name)    {        $value = null;        if (self::existsAttribute($name)) {            $value = $_SESSION[$name];            unset($_SESSION[$name]);        }        return $value;    }// Retorna un numero aleatorio    public static function getRandomNumber()    {        srand((double)microtime() * 1000000000);        $random_number = rand();        return $random_number;    }}